    teachinglessons,
    shoppingitems,
  }

 */

  const data = {
    events
  }

  console.log(data);

  return data;
}


export const loadAppData = () => async (dispatch: React.Dispatch<any>) => {
  dispatch(setLoading(true));
  const data = await getAppData();
 // dispatch(setData(data));


