import React, { useState, useRef } from 'react';
import { IonListHeader,IonThumbnail,IonContent, IonHeader, IonPage, IonToolbar, IonButton, IonSlides,IonSlide,IonButtons,IonIcon,useIonViewWillEnter, IonImg, IonTitle, IonText, IonCard, IonCardContent, IonCardSubtitle ,IonList, IonItem, IonAvatar, IonFooter, IonLabel, IonTabButton,IonTabBar, IonGrid, IonRow, IonCol, IonCardTitle, IonCardHeader,IonBackButton} from '@ionic/react';
import { arrowBack, arrowForward, bookmarksSharp , lockClosedOutline} from 'ionicons/icons';
import './Help.scss';
import { RouteComponentProps, withRouter } from 'react-router';
import { IonReactRouter } from '@ionic/react-router';
import { Link } from 'react-router-dom';
import { useHistory } from "react-router-dom";
import history from '../history';
import UserService from "../services/UserService";
import { BookmarkSharp,  HomeOutline , BagOutline, HeartOutline , PersonOutline ,ArrowBackOutline , LockClosedOutline ,ArrowForwardOutline} from 'react-ionicons'
type EventItem = {
    id: string;
    src: string;
    titre: string;
    text: string;
  };
  const items: EventItem[] = [{ id: '1', src: '/assets/img/israel_pilgrimage.jpg', titre: '2', text:'5'},
  {id: '1', src: '/assets/img/israel_pilgrimage.jpg', titre: '1', text:'4'},
  {id: '2', src: '/assets/img/israel_pilgrimage.jpg', titre: '2', text:'3'},
  { id: '3', src: '/assets/img/israel_pilgrimage.jpg', titre: '3', text:'2'}];


type SomeComponentProps = RouteComponentProps;
const Help: React.FC<SomeComponentProps> = ({ history }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [usernameError, setUsernameError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const login = async (e: React.FormEvent) => {
    e.preventDefault();
    setFormSubmitted(true);
    if(!username) {
      setUsernameError(true);
    }
    if(!password) {
      setPasswordError(true);
    }
    if(password && password ) {
      history.push('/tabs/home', {direction: 'none'});
    }
  };

    return (
        <IonPage id="teachingorder-page">
          <IonContent fullscreen>

                <IonButtons>
                    <IonBackButton defaultHref="/tabs/userprofile" className="back"/>
                </IonButtons>
                <IonText className="titre_order">  Need Help ?  </IonText>
              <br/>
              <br/>
              <br/>
              <hr className="trait_simple"></hr>
              <IonList className="event" lines="none">
            <IonListHeader className="titre_orders">Send your message </IonListHeader>
            <br/>
            <br/>

            <form noValidate onSubmit={login}>
          <IonList>
            <IonButton expand="block" color="facebook" className="margin"> 
            <LogoFacebook
            color={'#ffffff'}
            height="25px"
            width="25px"
              /> <IonText className="facebook">Facebook Connect</IonText>

              </IonButton>

            <IonButton expand="block" color="instagram" className="margin">
            <LogoInstagram
            color={'#ffffff'}
            height="25px"
            width="25px"/> <IonText className="instagram">Instagram Connect</IonText>
            </IonButton>

            <IonButton expand="block" color="twitter" className="margin"> 

            <LogoTwitter
            color={'#ffffff'}
            height="25px"
            width="25px"/> <IonText className="twitter">Twitter Connect</IonText>
            </IonButton>

            <IonText className="ion-text-center"> <p className="gray" > OR</p></IonText>

            <IonItem>
              <IonLabel position="stacked" color="primary">Email</IonLabel>
              <IonInput name="username" type="text" value={username} spellCheck={false} autocapitalize="off" onIonChange={e => setUsername(e.detail.value!)}
                required>
              </IonInput>
            </IonItem>

            {formSubmitted && usernameError && <IonText color="danger">
              <p className="ion-padding-start">
                Username is required
              </p>
            </IonText>}

            <IonItem>
              <IonLabel position="stacked" color="primary">Password</IonLabel>
              <IonInput name="password" type="password" value={password} onIonChange={e => setPassword(e.detail.value!)}>
              </IonInput>
            </IonItem>

            {formSubmitted && passwordError && <IonText color="danger">
              <p className="ion-padding-start">
                Password is required
              </p>
            </IonText>}
          </IonList>

          <IonRow>
            <IonCol>
              <IonButton className="login-button" type="submit" expand="block">Login</IonButton>
            </IonCol>
          </IonRow>
        </form>

             </IonList>



          </IonContent>

        </IonPage>


  );
}
export default withRouter (Help);
